pragma solidity ^0.4.21;

contract POT {

    struct BuyOrder {
        uint id;
        address owner;
        uint amount;
        uint maxPrice; 
    }

    struct SellOrder {
        uint id;
        address owner;
        uint amount;
        uint minrice; 
    }
    
    mapping (address => uint256) public balancesOfPOT;
    
    mapping (uint => BuyOrder) public buyOrders;

    mapping (uint => SellOrder) public sellOrders;


    uint public buyOrdersCount;
    uint public sellOrdersCount;
    
    uint public totalPOTbuyAmount;
    uint public totalPOTsellAmount;
    
    uint actualPrice=1;
    
    event buyOrderEvent (
        address buyOrderAddress, uint amount, uint maxPrice
    );

    event sellOrderEvent (
        address sellOrderAddress, uint amount, uint minPrice
    );
    
    constructor () public {
        balancesOfPOT[msg.sender] = 100;
        balancesOfPOT[0x14723A09ACff6D2A60DcdF7aA4AFf308FDDC160C]=30;
        balancesOfPOT[0x4B0897b0513fdC7C541B6d9D7E929C4e5364D2dB]=50;
        balancesOfPOT[0x583031D1113aD414F02576BD6afaBfb302140225]=60;
        totalPOTbuyAmount=0;
        totalPOTsellAmount=0;

    }
 
 
    function placeOrderBuy(uint _amountPOT, uint _maxPrice ) public payable returns(bool){
        //escrow the amount of money, but could approve this contract for allowance of the max money to pay
        buyOrdersCount++;
        buyOrders[buyOrdersCount] = BuyOrder(buyOrdersCount, msg.sender, _amountPOT, _maxPrice);

        totalPOTbuyAmount= totalPOTbuyAmount + _amountPOT;
        
        emit buyOrderEvent(msg.sender, _amountPOT, _maxPrice);
        return true;
    }
    
    function placeOrderSell(uint _amountPOT, uint _minPrice ) public payable returns(bool) {
        sellOrdersCount++;
        sellOrders[sellOrdersCount] = SellOrder(sellOrdersCount, msg.sender, _amountPOT, _minPrice);

        totalPOTsellAmount = totalPOTsellAmount + _amountPOT;
        
        emit sellOrderEvent(msg.sender, _amountPOT, _minPrice);
        return true;
    }
    
    function executeOrders() public returns(uint) {
        
        
        //mechanism to find actual price -> not interesting as it is pure algo 
        actualPrice = 3;
        
        totalPOTbuyAmount=0;
        totalPOTsellAmount=0;
        return actualPrice;
    }
 
    function whatIsActualPrice() external view returns(uint) {
        return actualPrice;
    }
    
    function balanceContract() public view returns(uint256) {
        address thisContract = this;
        return thisContract.balance;
    }

}
